<script>
import { gsap } from 'gsap'
export default {
	mounted() {
		this.animate()
	},
	methods: {
		animate() {
			const animationContainer = this.$el.querySelector('.js-animate')
			const elements = this.$el.querySelectorAll('.js-animate > *')

			const tl = gsap.timeline({
				scrollTrigger: {
					trigger: animationContainer,
					start: 'top 80%',
				},
			})

			tl.from(elements, {
				autoAlpha: 0,
				x: '50%',
			}).to(elements, {
				x: 0,
				stagger: {
					each: 0.1,
					ease: 'power2',
				},
				autoAlpha: 1,
			})
		},
	},
}
</script>

<style scoped>
section {
	background-color: var(--main-color);
	padding: 1rem;
	overflow: hidden;
}

section .img-container {
	width: 100%;
}

section .img-container svg {
	width: 100%;
	height: auto;
}

section .js-animate > * {
	visibility: hidden;
	opacity: 0;
	transform: translateX(50%);
}

@media screen and (min-width: 768px) {
	section {
		padding: 2em;
	}

	section .js-animate {
		display: grid;
		grid-template-columns: repeat(2, 1fr);
		grid-template-areas:
			'h img'
			'p img';
		align-items: center;
	}

	section h2 {
		grid-area: h;
	}

	section .body-text {
		grid-area: p;
		margin-right: 1em;
	}

	section .img-container {
		grid-area: img;
		margin-left: 1em;
		height: 40vw;
		width: 40vw;
		max-width: 600px;
		max-height: 600px;
		margin: auto;
	}
}
</style>
